    public function cek_no($no){
        $cek = intval(substr($no, 4,3));

        if($cek==999)
        {
           $this->form_validation->set_message('cek_no', 'Data telah mencapat batas pada bulan ini');
           return FALSE;
        } 
        return TRUE;
    }

    public function get_direktur(){
        header('Content-Type: application/json');
        $cek_direktur = $this->input->get('direktur_no');
        $cek = $this->M_direktur->get_direktur_by_no($cek_direktur);
        echo json_encode($cek);
    }


    function get_autocomplete(){
        if (isset($_GET['term'])) {
            $result = $this->M_direktur->search_direktur($_GET['term']);
            if (count($result) > 0) {
                foreach ($result as $row)
                $arr_result[] = array(
                        'label'         => $row->direktur_no,
                        'description'   => $row->direktur_nm,
                );
                echo json_encode($arr_result);
            }
        }
    }

    /*
     * Editing a direktur
     */
    function edit($direktur_id)
    {   
        if ($this->session->has_userdata('status') & ($this->session->userdata('level')!='Pejabat Lelang')) {
            // check if Data direktur exists before trying to edit it
            $data['direktur'] = $this->M_direktur->get_direktur($direktur_id);
            if (isset($data['direktur']['direktur_id'])) {
                if( $data['direktur']['direktur_jab']!=$this->session->userdata('level')){
                    $this->load->library('form_validation');
                    $eml =  ($this->input->post('direktur_eml') !=  $data['direktur']['direktur_eml']) ? '|is_unique[tb_direktur.direktur_eml]' : '';
                    $jab =  ($this->input->post('direktur_jab') !=  $data['direktur']['direktur_jab']) ? '|callback_cek_jab' : '';
                    $this->form_validation->set_rules('direktur_nm','Nama Lengkap','required|max_length[45]');
                    $this->form_validation->set_rules('direktur_tlp','Nomor Telepon','required|trim|min_length[10]|max_length[18]');
                    $this->form_validation->set_rules('direktur_alm','Alamat','required|max_length[255]');
                    $this->form_validation->set_rules('direktur_snd','Kata sandi','required|max_length[30]');
                    $this->form_validation->set_rules('direktur_eml','Email direktur','required|valid_email|max_length[30]'.$eml);
                    $this->rules();
                    if($this->form_validation->run())     
                    {   
                        $tgl = strtotime($this->input->post('direktur_tll'));
                        $tgl = date('Y-m-d',$tgl);
                        $params = array(
                            'direktur_nm' => $this->input->post('direktur_nm'),
                            'direktur_tlp' => trim($this->input->post('direktur_tlp')),
                            'direktur_alm' => $this->input->post('direktur_alm'),
                            'direktur_eml' => $this->input->post('direktur_eml'),
                            'direktur_snd' => $this->input->post('direktur_snd'),
                    );
                        $this->M_direktur->update_direktur($direktur_id,$params);    
                        redirect('direktur/index');
                }else{
                        $data['_view'] = 'v_direktur/edit';
                        $data['_landing'] = false;
                        $data['judul'] = "Data direktur";
                        $load = $this->loader->load($data);
                        $this->load->view('layouts/content',$load);
                    }
            }
            else
                show_error('Tidak memiliki akses ini.');
            }else{
                show_error('Data direktur yang diubah tidak ditemukan.');
            }
        }else{
            show_404();
        }
    }


