    private function kodeotomatis()
        {
            $res = $this->db->query('select max(right(direktur_no,11)) as `urut` from tb_direktur')->row();
            $tgl = date('/m.Y');
           // echo $res->urut.'<br>';
            if (isset($res->urut)) {
                //$urut = intval($res->urut);
                $urut = intval(substr($res->urut, 0,3));
                $bln = substr($res->urut, 4,2);
                $thn = substr($res->urut, 7,4);
/*                echo $urut.'<br>';
                echo $bln.'<br>';
                echo $thn.'<br>';
                echo substr($tgl, 1,2).'<br>';
                echo substr($tgl, 4,4).'<br>';*/
                if (substr($tgl, 1,2)==$bln) { ## bulan
                    if (substr($tgl, 4,4)==$thn) { ## tahun
                        if ($urut >= 999) {
                            $urut+=0;
                        }else{
                            $urut++;
                        }
                    }else{

                        $urut=1;
                    }
                }else{
                    $urut=1;
                }
            }else{
                $urut=1;
            }
            $str_length = strlen($urut);
            $dig = substr("000{$urut}", $str_length);
            return 'DIR-'.$dig.$tgl;
        }


    function info($direktur_id)
    {   
        if ($this->session->has_userdata('status')) {
            // check if Data direktur exists before trying to edit it
            $data['direktur'] = $this->M_direktur->get_direktur($direktur_id);
            
            if(isset($data['direktur']['direktur_id']))
            {
                $data['_view'] = 'v_direktur/info';
                $data['_landing'] = false;
                $data['judul'] = "Data direktur";
                $load = $this->loader->load($data);
                $this->load->view('layouts/content',$load);
            }
            else
                show_error('Data direktur tidak ditemukan.');
        }else{
            show_404();
        }
    }
