    public function login()
    {
        $this->load->library('form_validation');
        $this->form_validation->set_rules('u_email','Email atau NIP','required');
        $this->form_validation->set_rules('u_password','Password','required');
        if ($this->form_validation->run()) {
            $email = $this->input->post('u_email');
            $password = $this->input->post('u_password');
            $data['user'] = $this->M_direktur->get_login($email,$password);
            if (isset($data['user']['direktur_id'])) {
                $data['direktur'] = $this->M_direktur->get_direktur_by_no($data['user']['direktur_no']);
                $login = array(
                    'id_login' => $data['user']['direktur_id'],
                    'nip' => $data['user']['direktur_no'],
                    'nama' => $data['user']['direktur_nm'],
                    'email' => $data['user']['direktur_eml'],
                    'level' => 'Direktur',
                    'sts' => $data['direktur']['direktur_sts'],
                    'status' => "login"
                );
                $this->session->set_userdata($login);
                return $this->output
                    ->set_content_type('application/json')
                    ->set_status_header(200)
                    ->set_output(json_encode(array(
                            'code' => 200,
                            'class' => '-success',
                            'desc' => 'Anda Berhasil Login!'
                    )));
            }else{
                return $this->output
                    ->set_content_type('application/json')
                    ->set_status_header(200)
                    ->set_output(json_encode(array(
                            'code' => 205,
                            'class' => '-danger',
                            'desc' => 'Email/NIP atau Katasandi anda salah! silahkan coba lagi'
                    )));
            }
        }else{
            if(!$this->session->has_userdata('status')){
                $data['_view'] = 'v_direktur/login';
                $data['_landing'] = true;
                $this->load->view('layouts/content',$data);   
            }else{
                redirect('dash');
            }
        }
    }

    function logout(){
        $this->session->sess_destroy();
        redirect('login');
    }

    function lupa_sandi()
    {
        if ($this->session->has_userdata('status')) {
            redirect('dash');
        }else{
            $this->load->library('form_validation');
            $this->form_validation->set_rules('u_email','Email','required');
            if ($this->form_validation->run()) {
                $direktur_eml = $this->input->post('u_email');
                $data['direktur'] = $this->M_direktur->is_direktur_with_email($direktur_eml);
                if (isset($data['direktur']['direktur_snd'])) {
                 $pesan =  'ID : '.$data['direktur']['direktur_id'].'<br>'.'Email : '.$data['direktur']['direktur_eml'].'<br>'.'Kata sandi : '.$data['direktur']['direktur_snd'];
                    $kirim = array(
                        'pengirim'=>'KPKNL BEKASI',
                        'penerima'=>$data['direktur']['direktur_eml'],
                        'pesan'=>$pesan,
                        'judul'=>'Lupa kata sandi direktur',
                    );
                    $this->kirim_email($kirim);
            echo '<script>alert("Ditemukan ! periksa kontak masuk pada alamat email '.$data['direktur']['direktur_eml'].' untuk melihat data.")</script>';
                    echo 'Ditemukan ! periksa kontak masuk pada alamat email '.$data['direktur']['direktur_eml'].' untuk melihat data.';
                }else{
                     echo '<script>alert("Email tidak valid.")</script>';
                redirect('forgot');
                }
             }else{
                 $data['_view'] = 'v_home/v_lupa';
                 $data['_landing'] = true;
                 $this->load->view('layouts/main',$data);
             }   
        }
    }

    private function kirim_email($konten='')
    {
        $config = $this->loader->config_email();
        $this->load->library('email', $config);
        $this->email->from($config['smtp_user'],   $konten['pengirim']);
        // Email penerima
        $this->email->to($konten['penerima']); // Ganti dengan email tujuan

        // Lampiran email, isi dengan url/path file
        if (isset($konten['lampiran'])) {
            $this->email->attach($konten['lampiran']);
        }

        // Subject email
        $this->email->subject($konten['judul']);

        // Isi email
        $this->email->message($konten['pesan']);

        $status = $this->email->send();
        if ($status) {
            sleep(3);
            echo '<script>alert(Sukses! Mohon periksa kontak masuk pada alamat email '.$konten['penerima'].');</script>';
            //redirect('login');
        } else {
            sleep(3);
            echo '<script>alert(Email tidak dapat dikirim.)</script>';
        }
    }

    
}
